import { useState } from "react"

import Card from "../../components/Card/Card"
import Product from "../../components/Product/Product"

const Home = () => {
    const [numLemons, updateNumLemons] = useState(0)
    return (
        <div className="Home">
            <Card>
                <Product
                    name="Lemons"
                    amount={numLemons}
                    increment={() => updateNumLemons(numLemons + 1)}
                    decrement={() =>
                        updateNumLemons(numLemons > 0 ? numLemons - 1 : 0)}
                />
                <Product name="Sugar" amount={0} />
                <Product name="Ice Cubes" amount={0} />
            </Card>
        </div>
    )
}

export default Home
