import styled from "styled-components";

const Button = styled.button`
    width: ${({ w }) => w};
    height: ${({ h }) => h};
    border: 1px solid black;
    background: ${({ bg }) => bg};
    color: #555;
    font-size: 20px;
    &:disabled {
        background: #ccc;
    }
`
export default Button
